import { Component, EventEmitter, Input, OnDestroy, OnInit, Output } from '@angular/core';
import { lastValueFrom, Subscription } from 'rxjs';
import { UploadFileService } from '../services/upload-file.service';
import { SendFileService } from '../services/send-file.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent implements OnInit, OnDestroy {
  @Input() acceptedFormat: string = '.pdf';
  @Input() maxSize: number = 102400;
  @Output() onSendFilesList: EventEmitter<FileItem[]> = new EventEmitter<
    FileItem[]
  >();
  // filesList = [];
  uploadedFiles: any = [];
  currentFolder: any = "";

  message: any;
  subscription: Subscription = new Subscription;

  constructor(private uploadFileService: UploadFileService, private data: SendFileService) { }
  ngOnInit() {
    this.subscription = this.data.currentMessage.subscribe((message: any) => this.message = message)
  }

  ngOnDestroy(): void {
    this.subscription.unsubscribe();
  }

  async UploadFiles(files: any) {
    const folderHolder: any = {};
    this.uploadedFiles = [];

    let file: any;
    for (file of Array.from(files)) {
      // console.log(file['webkitRelativePath'], file['size'], file['type']);
      const path: string = file['webkitRelativePath'];
      const pathPieces: any = path.split('/');

      if (!pathPieces.reverse()[0].startsWith('.')) {
        this.currentFolder = pathPieces[1];
        // console.log('Checking for folder ', currentFolder, 'in', folderHolder);
        if (folderHolder[this.currentFolder]) {
          const b = await lastValueFrom(
            this.uploadFileService.uploadFile(folderHolder[this.currentFolder], file)
          );
          this.uploadedFiles.push({
            fileName: pathPieces[0],
            folder: this.currentFolder,
            folderId: folderHolder[this.currentFolder],
          });
        } else {
          const folderId = await lastValueFrom(
            this.uploadFileService.createFolder(this.currentFolder)
          );
          folderHolder[this.currentFolder] = folderId;

          const p = await lastValueFrom(
            this.uploadFileService.uploadFile(folderHolder[this.currentFolder], file)
          );
          this.uploadedFiles.push({
            fileName: pathPieces[0],
            folder: this.currentFolder,
            folderId: folderHolder[this.currentFolder],
          });
        }
      }
    }
    console.log(this.uploadedFiles[0].folder);
  }
  newMessage() {
    this.data.changeMessage(this.uploadedFiles);
  }
}

export class FileItem {
  name: string = "";
  size: number = 0;
  type: string = "";
}
<input
  id="1-basic"
  type="file"
  #file_input
  (change)="UploadFiles(file_input.files)"
  webkitdirectory
  multiple
/>
<div style="width: 80%; margin-top: 30px">
  <ng-container *ngIf="uploadedFiles?.length">
    Uploaded {{ currentFolder }}.
  </ng-container>
</div>

