  handleFolderUpload(event: any) {
    const files = event.target.files;
    if (files.length > 0) {
      // Iterate through the selected folders
      for (let i = 0; i < files.length; i++) {
        const folder = files[i];
        // You can now work with the folder and its contents as needed
        console.log("Selected folder:", folder);
        // For example, you can access the files inside the folder
        const filesInsideFolder = folder.webkitGetAsEntry().createReader().readEntries();
        filesInsideFolder.then((entries: any[]) => {
