import { Component, OnInit } from '@angular/core';
import { UploadFileService } from '../services/upload-file.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent implements OnInit {
  ngOnInit() {}

  UploadFolder(): void {
    let files: FileList | null = document.getElementById("files").files;
    for (let file = 0; file < files.length; file++) {
        const reader: FileReader = new FileReader();
        reader.onload = function(e: ProgressEvent<FileReader>): void {
            const object: { content: string } = new Object();
            object.content = e.target?.result as string;
            const json_upload: string = "jsonObject=" + JSON.stringify(object);
            const xmlhttp: XMLHttpRequest = new XMLHttpRequest();
            xmlhttp.open("POST", "http://localhost:8080/readFileServlet");
            xmlhttp.setRequestHeader("Content-Type", "application/x-www-form-urlencoded");
            xmlhttp.send(json_upload);
        }
        reader.readAsBinaryString(files[file]);
    }
}
}
